import Navbar from './components/navbar';
import Home from './components/HomeView';
import About from './components/AboutView';
import  SearchView  from './components/SearchView';
import './App.css';
import { Route, Routes } from 'react-router-dom';
import { useState, useEffect } from 'react';

// api key: 9824627a2bab695112c4a6fc02bb3277


function App() {

  const [searchResult, setSearchResult] = useState([])
  const [searchText, setSearchText]= useState('')

  const options = {
    method: 'GET',
    headers: {
      accept: 'application/json',
      Authorization: 'Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiI5ODI0NjI3YTJiYWI2OTUxMTJjNGE2ZmMwMmJiMzI3NyIsInN1YiI6IjY0NmRiZWM2NTRhMDk4MDBlNDExNjE3ZCIsInNjb3BlcyI6WyJhcGlfcmVhZCJdLCJ2ZXJzaW9uIjoxfQ.TK9J4LlsL3vQ3HNBnSXGyTLh0gyLHTCbm1ABW2sQ1bo'
    }
  };

  fetch('https://api.themoviedb.org/3/movie/11?language=en-US', options)
    .then(response => response.json())
    .then(response => console.log(response))
    .catch(err => console.error(err));
  useEffect(()=>{
    if(searchText){
      console.log(searchText, 'is the search text')
      fetch(`https://api.themoviedb.org/3/movie/11?api_key=9824627a2bab695112c4a6fc02bb3277&language=en-US&query=${searchText}&page=1`)
      .then(Response => Response.json())
      .then(data =>{
        setSearchResult(data.results)
      })
      }
    },[searchText])


  return (
    <div>
      <Navbar searchText={searchText} setSearchText={setSearchText}/>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<About />} />
        <Route path="/search" 
        element= {<SearchView keyword={searchText} searchResult={searchResult}/>} />
      </Routes>
    </div>
  );
}

export default App;
