import {  Link } from "react-router-dom";
import { useNavigate } from "react-router-dom";
import SearchView from "./SearchView";
const Navbar = ({searchText, setSearchText}) => {

  const navigate= useNavigate()
  const updateSearchText = (e) =>{
    navigate('/search')
    setSearchText(e.target.value)
  }
  const clickHandle= (e) =>{
    navigate('/search/movies')
    setSearchText(e.target.value)
    return (<SearchView />)
  }

    return (
      <div>
      <nav className="navbar navbar-expand-lg bg-body-tertiary">
        <div className="container-fluid">
          <Link className="navbar-brand" to="/">MovieBros</Link>
          <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" 
          aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarSupportedContent">
            <ul className="navbar-nav me-auto mb-2 mb-lg-0">
              <li className="nav-item">
                <Link className="nav-link active" aria-current="page" to="/">Home</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="/about">About</Link>
              </li>
            </ul>
            <form className="d-flex" role="search">
              <input className="form-control me-2" type="search" placeholder="Search"
               aria-label="Search" value={searchText} onChange={updateSearchText} />
              <button className="btn btn-outline-success" type="submit" onClick={clickHandle}>Search</button>
            </form>
          </div>
        </div>
      </nav>
      </div>
    )
  }

  export default Navbar;
