import drop from "./backdropimng.jpeg"

const Hero = ({ text, backdrop }) => {
    let bkdrop;
    if(backdrop.substr(backdrop.length -4) === null) bkdrop= drop;
    else bkdrop= backdrop;
    return (
      <header className="bg-dark text-white p-5 hero-container">
        <h1 className="hero-text">{text}</h1>
        {backdrop &&
        <div className="hero-backdrop" style={{backgroundImage:`url(${bkdrop})` }} ></div>        
        }
      </header>
    )
  }

  export default Hero;
